INSERT INTO product (product_name, description, price, photo_url) VALUES
('Mercedes-Benz A-Class', 'The Mercedes-Benz A-Class represents a harmonious blend of style, innovation, and performance, setting a new standard in the compact luxury car segment. Perfectly crafted for drivers who demand sophistication in a smaller package, the A-Class combines cutting-edge technology with the hallmark elegance and refinement that Mercedes-Benz is known for.', 7000000.00, 'https://upload.wikimedia.org/wikipedia/commons/f/f7/2018_Mercedes-Benz_A200_AMG_Line_Premium_Automatic_1.3_Front.jpg'),
('Mercedes-Benz C-Class', 'The Mercedes-Benz C-Class is a remarkable blend of timeless luxury, cutting-edge technology, and dynamic performance. As one of the flagship models in the Mercedes-Benz lineup, the C-Class exemplifies the brands dedication to delivering an unparalleled driving experience. Whether you are seeking refined comfort, exhilarating performance, or advanced innovation, the C-Class offers it all in one sophisticated package.', 5000000.00, 'https://upload.wikimedia.org/wikipedia/commons/b/be/Mercedes-Benz_W206_IMG_6380.jpg'),
('Mercedes-Benz E-Class', 'The Mercedes-Benz E-Class is the benchmark for executive sedans, offering a seamless fusion of elegance, cutting-edge technology, and dynamic performance. Renowned for its sophistication and innovative features, the E-Class redefines what it means to drive in comfort and style, making every journey extraordinary.', 5500000.00, 'https://upload.wikimedia.org/wikipedia/commons/f/fd/Mercedes-Benz_W214_1X7A1841.jpg'),
('Mercedes-Benz GLC', 'The Mercedes-Benz E-Class is the benchmark for executive sedans, offering a seamless fusion of elegance, cutting-edge technology, and dynamic performance. Renowned for its sophistication and innovative features, the E-Class redefines what it means to drive in comfort and style, making every journey extraordinary.', 6500000.00, 'https://upload.wikimedia.org/wikipedia/commons/2/2c/Mercedes-Benz_X254_1X7A6343.jpg'),
('Mercedes-Benz S-Class', 'The Mercedes-Benz S-Class represents the height of sophistication, innovation, and performance in the luxury automotive world. As the flagship model of Mercedes-Benz, the S-Class redefines excellence, delivering an unparalleled experience in comfort, technology, and refinement. This iconic sedan is designed for those who demand nothing but the best, whether behind the wheel or seated in the lap of luxury.', 9000000.00, 'https://upload.wikimedia.org/wikipedia/commons/5/55/Mercedes-Benz_W223_IMG_6663.jpg');
